import './App.css';
import React from 'react';
import Modal from 'react-modal';
import { useState } from 'react';

// make dictionary for menu
const menu1 = {name:"gimbab", price:3000, description:"description"}
const menu2 = {name:"ramyun", price:3000, description:"description"}
const menu3 = {name:"topoki", price:8000, description:"description"}
const menu4 = {name:"rapoki", price:5000, description:"description"}
const menu5 = {name:"dongas", price:9000, description:"description"}


function App() {

  const [modalIsOpen, setModalIsOpen] = useState(false);
  const onclickmenu = event=>{
    setModalIsOpen(true);
    return String(event.currentTarget.id)
  }
  const menu_id={onclickmenu}



return (
    <div className="App">
    {/* id값을 통해 어떤 메뉴인지 일단 판단 */}
    <button id="menu1" onClick={onclickmenu}>Go to detnail_menu</button>

    <div>    
    <Modal isOpen={modalIsOpen}>

    <div className='menu'>
      <div>{menu_id['name']} </div>
      <div>{menu_id['price']} </div>
      <div>{menu_id['description']} </div>
    </div>

    </Modal>
    </div>



    </div>
  );
}

export default App;
