import axios from "axios";
import { useEffect, useState } from "react";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";

const CategoryList = () => {
  //모달 창 상태 변수
  const [show, setShow] = useState(false);
  //카테고리 데이터를 담을 변수
  const [category, setCategory] = useState([]);
  // 카테고리 추가 함수
  const PlusCategory = async (event) => {
    console.log("EVENT", event);
    await axios
      .post("http://127.0.0.1:8000/webKiosk/category/create/", {
        market_name: "S",
        category_name: "category",
      })
      .then((response) => {
        console.log("response", response);
      });
  };

  const Category = async () =>
    await axios
      .post("http://127.0.0.1:8000/webKiosk/client/category/read/", {
        market_name: "S",
      })
      .then((response) => {
        setCategory(response.data);
      });
  useEffect(() => {
    Category();
  }, []);

  return (
    <>
      <ul>
        {Object.values(category).map((item) => {
          return (
            <>
              <li>{item.category_name}</li>
              <button>삭제</button>
            </>
          );
        })}
      </ul>
      <button onClick={() => setShow(true)}>추가</button>
      <Modal show={show}>
        <h2>새 카테고리 이름을 입력해주세요</h2>
        <Form.Control size="lg" type="text" placeholder="Large text" />
        <button type="submit" onClick={() => PlusCategory()}>
          확인
        </button>
        <button> 취소</button>
      </Modal>
    </>
  );
};

export default CategoryList;
