import "./MenuTap.css";
import Tab from "react-bootstrap/Tab";
import Tabs from "react-bootstrap/Tabs";
import AllMenuList from "./AllMenuList";
import { useEffect, useState } from "react";
import axios from "axios";
import MenuList from "./MenuList";
function MenuTap() {
  const [Alldata, setAlldata] = useState([]);
  const [key, setKey] = useState("0");

  const allMenu = async () => {
    await axios
      .post("http://127.0.0.1:8000/webKiosk/download/", {
        market_name: "S",
      })
      .then((response) => {
        setAlldata(response.data);
      });
  };
  useEffect(() => {
    allMenu();
  }, []);
  console.log("alldata", Alldata);
  if (Alldata === null || Alldata === undefined) {
    return <h2>메뉴가 없습니다.</h2>;
  }
  return (
    <Tabs
      defaultActiveKey="0"
      id="uncontrolled-tab-example"
      className="mb-3"
      // activeKey={key}
      // onSelect={(k) => setKey(k)}
    >
      {Object.keys(Alldata).map((item) => {
        return (
          <Tab
            key={item}
            eventKey={item}
            title={Alldata[item]["category_name"]}
          >
            <MenuList
              categoryName={Alldata[item]["category_name"]}
              SelectTap={Alldata[key]["category_name"]}
            />
          </Tab>
        );
      })}
      <Tab title={"전체메뉴"} eventKey={"0"}>
        >
        <AllMenuList />
      </Tab>
    </Tabs>
  );
}

export default MenuTap;
