import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import axios from "axios";
import React, { useState } from "react";

const OptionCreate = ({ show }) => {
  //옵션 종류 추가하는 기능
  const [inputList, setInputList] = useState([{ Name: "", Price: "" }]);
  // handle input change
  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const list = [...inputList];
    list[index][name] = value;
    setInputList(list);
  };

  // handle click event of the Remove button
  const handleRemoveClick = (index) => {
    const list = [...inputList];
    list.splice(index, 1);
    setInputList(list);
  };

  // handle click event of the Add button
  const handleAddClick = () => {
    setInputList([...inputList, { firstName: "", lastName: "" }]);
  };

  function handlesubmit(event) {
    console.log("event", event.target.length);
    event.preventDefault();
    const option_name = event.target[0].value;
    const option_list = {};
    for (let i = 1; i < event.target.length - 2; i++) {
      if (i % 2 == 0) {
        option_list[event.target[i - 1].value] = Number(event.target[i].value);
      }
    }

    console.log(JSON.stringify(option_list));

    const data = async () =>
      await axios
        .post("http://127.0.0.1:8000/webKiosk/client/option/create/", {
          market_name: "S",
          option_name: option_name,
          option_list: JSON.stringify(option_list),
        })
        .then((response) => {
          console.log(response);
        });
    data();
    show(false);
  }

  // axios
  return (
    <Form onSubmit={(e) => handlesubmit(e)}>
      <Row className="mb-3">
        <Form.Group as={Col} controlId="formGridEmail">
          <Form.Label>옵션 이름</Form.Label>
          <Form.Control type="text" placeholder="Enter email" />
        </Form.Group>
      </Row>
      <Form.Group className="mb-3" controlId="formGridAddress1">
        <Row>
          <Col>
            <Form.Label>항목1 이름</Form.Label>
            <Form.Control placeholder="이름을 입력해주세요" />
          </Col>
          <Col>
            <Form.Label>항목1 가격</Form.Label>
            <Form.Control type="Number" placeholder="Last name" min="0" />
          </Col>
        </Row>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formGridAddress1">
        <Row>
          <Col>
            <Form.Label>항목1 이름</Form.Label>
            <Form.Control placeholder="이름을 입력해주세요" />
          </Col>
          <Col>
            <Form.Label>항목1 가격</Form.Label>
            <Form.Control type="Number" placeholder="Last name" min="0" />
          </Col>
        </Row>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formGridAddress1">
        <Row>
          <Col>
            <Form.Label>항목3 이름</Form.Label>
            <Form.Control placeholder="이름을 입력해주세요" />
          </Col>
          <Col>
            <Form.Label>항목3 가격</Form.Label>
            <Form.Control type="Number" placeholder="Last name" min="0" />
          </Col>
        </Row>
      </Form.Group>
      <div className="App">
        <h3>옵션 세부 항목 설정</h3>
        {inputList.map((x, i) => {
          return (
            <div className="box">
              <Form.Group className="mb-3" controlId="formGridAddress1">
                <Row>
                  <Col>
                    <Form.Label>{i + 1}번 항목 이름</Form.Label>
                    <Form.Control placeholder="이름을 입력해주세요" />
                  </Col>
                  <Col>
                    <Form.Label>{i + 1}번 항목 가격</Form.Label>
                    <Form.Control
                      type="Number"
                      placeholder="가격을 입력하세요"
                      min="0"
                      value={x.Price}
                    />
                  </Col>
                </Row>
              </Form.Group>
              <input
                name="firstName"
                placeholder="Enter First Name"
                value={x.firstName}
                onChange={(e) => handleInputChange(e, i)}
              />
              <input
                className="ml10"
                name="lastName"
                placeholder="Enter Last Name"
                value={x.lastName}
                onChange={(e) => handleInputChange(e, i)}
              />
              <div className="btn-box">
                {inputList.length !== 1 && (
                  <button className="mr10" onClick={() => handleRemoveClick(i)}>
                    Remove
                  </button>
                )}
                {inputList.length - 1 === i && (
                  <button onClick={handleAddClick}>Add</button>
                )}
              </div>
            </div>
          );
        })}
        <div style={{ marginTop: 20 }}>{JSON.stringify(inputList)}</div>
      </div>
      <Button variant="primary" type="submit">
        생성하기
      </Button>
      <Button type="button" onClick={() => show(false)}>
        취소하기
      </Button>
    </Form>
  );
};

export default OptionCreate;
