import axios from 'axios';
import { useEffect, useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Edit from 'src/Menu/MenuEdit/Edit';
const MenuList=({categoryName})=>{
  //메뉴 데이터를 담을 변수
  console.log("categoryName",categoryName)
  const[Alldata,setAlldata]=useState([]);
  //전체 카테고리별 메뉴를 Alldata에 저장
  const allMenu=async()=>{await axios.post('http://127.0.0.1:8000/webKiosk/client/meca/read/',
  {
    "market_name" : "S",
    "category_name" : categoryName
  }).then(
    (response)=>{
      setAlldata(response.data)
    }
  );
}

useEffect(()=>{
  allMenu();
},[])

    //Alldata 데이터가 없을때 처리
  if (Alldata===null || Alldata===undefined){
    return(
      <h2>메뉴가 없습니다.</h2>
    )
  }
  function MenuStatus(logic){
    if(logic==true){
      return "판매중지"
    }
    else{
        return "판매중"
        }
  }
  //리스트 작성 파트
  let index=0;
    return(
            <div className="tableContainer">
            <table className="table">
            <thead>
                <tr id="tre">
                <th className="col">#</th>
                <th className="col">이름</th>
                <th className="col">가격</th>
                <th className="col">판매상태</th>
                <th className="col"></th>
                </tr>
            </thead>
            <tbody>
            {Object.keys(Alldata).map((menuindex)=>{

                    console.log("item2")
                    return(
                        <tr id={index}>
                        <th>{index++}</th>
                        <td>{Alldata[menuindex].menu_name}</td>
                        <td>{Alldata[menuindex].price+"원"}</td>
                        <td>{MenuStatus(Alldata[menuindex].is_forbidden)}</td>
                        <td><button>편집</button></td>
                        <Modal>
                            <Edit/>
                        </Modal>
                        </tr>
                        
                        )
                    }
                    ) 
                    }
                </tbody>
            </table>
            
            </div>
            
        )
    }
  

export default MenuList;
