import axios from "axios";
import { useEffect, useState } from "react";

function Edit({ MenuInfo }) {
  const [categoryList, setCategoryList] = useState([]);
  useEffect(() => {
    console.log("MenuInfo", MenuInfo);
    LoadCate();
  }, []);
  const LoadCate = async () => {
    const response = await axios
      .post("http://127.0.0.1:8000/webKiosk/client/category/read/", {
        market_name: "S",
      })
      .then((response) => {
        setCategoryList(response.data);
      });
  };
  return (
    //make form for edit menu
    <div>
      <form>
        <div>
          <label>메뉴이름</label>
          <input type="text" defaultValue={MenuInfo.menu_name} />
        </div>
        <div>
          <label>가격</label>
          <input type="number" defaultValue={MenuInfo.price} />
        </div>
        <div>
          {/* {//make category dropdown} */}
          <label>카테고리</label>
          <select>
            {Object.values(categoryList).map((item) => {
              if (MenuInfo.category_name === item.category_name) {
                console.log("selected item", item);

                return (
                  <option value={item.category_name} selected="selected">
                    {item.category_name}
                  </option>
                );
              } else {
                return (
                  <option value={item.category_name}>
                    {item.category_name}
                  </option>
                );
              }
            })}
          </select>
        </div>
        <div>
          <label>사진</label>
          <input type="text" value={MenuInfo.image} />
        </div>
        <div>
          <label>설명</label>
          <input type="text" value={MenuInfo.description} />
        </div>
        <div>
          <label>옵션</label>
          <input type="text" value={MenuInfo.option} />
        </div>
        <div>
          <label>옵션가격</label>
          <input type="text" value={MenuInfo.option_price} />
        </div>
      </form>
    </div>
  );
}

export default Edit;
