import axios from "axios";
import { useEffect, useState } from "react";

function Edit({ MenuInfo }) {
  const [categoryList, setCategoryList] = useState([]);

  const LoadOption = async () => {
    const response = await axios
      .post("http://127.0.0.1:8000/webKiosk/client/opme/read/", {
        market_name: "S",
        menu_name: MenuInfo.menu_name,
      })
      .then((response) => {
        setCategoryList(response.data);
      });
  };

  useEffect(() => {
    console.log("MenuInfo", MenuInfo);
    LoadOption();
  }, []);

  return (
    //make form for edit menu
    <div>
      <form>
        <div>
          <label>메뉴이름</label>
          <input type="text" defaultValue={MenuInfo.menu_name} />
        </div>
        <div>
          <label>가격</label>
          <input type="number" defaultValue={MenuInfo.price} />
        </div>
        <div>
          {/* {//make soldout/on sale dropdown} */}
          <label>메뉴상태</label>
          <select>
            <option value="onsale">판매중</option>
            <option value="notsale" selected={MenuInfo.is_forbidden}>
              판매중지
            </option>
          </select>
        </div>
        <div>
          <label>사진</label>
          <input type="text" value={MenuInfo.image} />
        </div>
        <div>
          <label>옵션</label>
          <input type="text" value={MenuInfo.option} />
        </div>
        <div>
          <label>옵션가격</label>
          <input type="text" value={MenuInfo.option_price} />
        </div>
        <div>
          <button type="submit">메뉴수정</button>
        </div>
      </form>
    </div>
  );
}

export default Edit;
