import axios from "axios";
import { useEffect, useState } from "react";

function Edit({ MenuInfo, IsModalOpen, rerender }) {
  const [optionList, setoptionList] = useState([]);
  const LoadOption = async () => {
    const response = await axios
      .post("http://127.0.0.1:8000/webKiosk/client/opme/read/", {
        market_name: "S",
        menu_name: MenuInfo.menu_name,
      })
      .then((response) => {
        setoptionList(response.data);
      });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("EVENT", event);
    let selectedElement = document.querySelector("#status");
    let NewName = event.target[0].value;
    const IsSoldOut = selectedElement.value;
    const newPrice = Number(event.target[1].value);
    console.log(IsSoldOut, NewName, newPrice);
    axios
      .post("http://127.0.0.1:8000/webKiosk/client/menu/update/", {
        market_name: "S",
        old_name: `${MenuInfo.menu_name}`,
        new: `{
          "menu_name" : "${NewName}"
          "price" : ${newPrice}
        }`,
      })
      .then((response) => {
        if (response.data === "이미 동일한 이름이 존재합니다.") {
          alert(response.data);
        } else {
          IsModalOpen(false);
          window.location.reload();
        }
      });
  };
  useEffect(() => {
    console.log("MenuInfo", MenuInfo);
    LoadOption();
  }, []);

  return (
    //make form for edit menu
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>메뉴이름</label>
          <input
            type="text"
            name="menu_name"
            id="NewName"
            defaultValue={MenuInfo.menu_name}
          />
        </div>
        <div>
          <label>가격</label>
          <input
            type="number"
            name="menu_price"
            id="NewPrice"
            defaultValue={MenuInfo.price}
          />
        </div>
        <div>
          <label>메뉴상태</label>
          <select id="status">
            <option value={false}>판매중</option>
            <option value={true} seledcted={MenuInfo.is_forbidden}>
              판매중지
            </option>
          </select>
        </div>
        <div>
          <label>사진</label>
          <input type="text" value={MenuInfo.image} />
        </div>
        <div>
          <label>옵션</label>
          <select>
            {optionList.map((item) => {
              console.log("item", item);
              console.log("optionlist", optionList);
              return (
                <option value={item.option_name} selected={item.is_forbidden}>
                  {item.option_name}
                </option>
              );
            })}
          </select>
        </div>
        <div>
          <label>옵션가격</label>
          <input type="text" value={MenuInfo.option_price} />
        </div>
        <div>
          <button type="submit">메뉴수정</button>
          <button type="button" onClick={() => IsModalOpen(false)}>
            취소
          </button>
        </div>
      </form>
    </div>
  );
}

export default Edit;
